mapper.update:
  	UPDATE timetable a 
	SET a.status = 0
	WHERE a.semester IN (
	    SELECT semester 
	    FROM timetable
	    WHERE timetable_id = #{timetable_id}
	) AND user_id = #{user_id}

        UPDATE TIMETABLE 
	    	SET NAME = #{NAME},
	        TIMETABLE_DATE = SYSDATE,
	        STATUS = #{STATUS}
	    WHERE TIMETABLE_ID = #{TIMETABLE_ID}

mapper.select:
SELECT MAX(TIMETABLE_ID)
FROM TIMETABLE
WHERE USER_ID = #{USER_ID}

SELECT COUNT(*) AS CNT
FROM TIMETABLE
WHERE user_id = #{user_id} and SEMESTER = #{semester}

    select t.name from (
		select * from TIMETABLE
			where name like '시간표%'
			and user_id = #{user_id}
			and semester = #{semester}
		order by name desc
		) t
	where rownum =1

      SELECT TIMETABLE_ID, USER_ID, NAME, SEMESTER, 
	           TO_CHAR(TIMETABLE_DATE,'MM/DD HH24:MI') as TIMETABLE_DATE, STATUS 
       FROM TIMETABLE WHERE TIMETABLE_ID = #{TIMETABLE_ID}
