mapper.select:
select anonymous
from board
where board_id = #{board_id}

select content
from board
where board_id = #{board_id}

mapper.update:
UPDATE board
SET content = #{content}
WHERE board_id = #{board_id}

mapper.select:
select count(*)
from board
where board_id = ${board_id} and user_id = ${user_id}

select count(*)
from board
where name = '${board_name}' and board_id = #{board_id}

mapper.update:
update board
set user_id = #{tf_user_id_num}
where user_id = #{am_user_id_num} and board_id = #{board_id}

mapper.select:
select count(*)
