mapper.update:
UPDATE BOARD
SET STATUS = #{approvalStatus}
WHERE BOARD_ID = #{board_id}

UPDATE BOARD
SET STATUS = #{approvalStatus},REJECT_REASON = #{rejectionReason}
WHERE BOARD_ID = #{board_id}

mapper.select:
SELECT * FROM
	( SELECT ROWNUM RNUM, B.*
	  FROM
			( SELECT B.*, M.LOGIN_ID login_id, S.NAME school_name 
			  FROM BOARD B INNER JOIN MEMBER M
			  ON B.USER_ID = M.USER_ID
			  INNER JOIN SCHOOL S 
			  ON B.SCHOOL_ID = S.SCHOOL_ID
			  WHERE B.STATUS = 1 OR B.STATUS = 2
			 )B
			  WHERE ROWNUM < = #{end}
	 )
WHERE RNUM > = #{start} AND RNUM < = #{end}


	select anonymous
	from board
	where board_id = (	select board_id
						from post
						where post_id = #{num}
					)
	or board_id =	( 	select board_id
						from post
						where post_id = 
									(	select post_id
										from comments
										where comment_id = #{num}
									)
						
					)
