mapper.select:
		SELECT * FROM (
		    SELECT ROWNUM RNUM, j.* 
		    FROM (SELECT post.* 
                FROM post 
		        	WHERE post.board_id = #{board_id}
		        	AND post.status = 1
		        	
				ORDER BY post.post_id DESC) j
			         WHERE ROWNUM <= #{end}
				)
			WHERE RNUM >= #{start} AND RNUM <= #{end}

select report_count 
from post
where post_id = #{post_id}

mapper.update:
UPDATE post
SET scrap_count = scrap_count + 1
WHERE post_id = #{post_id}

UPDATE post
SET scrap_count = scrap_count - 1
WHERE post_id = #{post_id}
